# coding=utf-8
from pyspark.sql import SparkSession
from pyspark.sql.types import *

spark = SparkSession \
    .builder \
    .appName("user") \
    .config("spark.some.config.option", "some-value") \
    .getOrCreate()
spark.sparkContext.setLogLevel('WARN')

# 首先根据用户行为得分方案，求出用户对每频道各主题词得分，用户特征为各频道最高k个得分

# 一、用户文章行为信息联合文章主题信息
# 用户文章行为数据：user_id, action_time, article_id, channel_id, shared, clicked, collected, exposure, read_time
user_article_basic = spark.sparkContext.textFile(r'data\backup\profile.db\user_article_basic')
# user_article_basic = spark.sparkContext.textFile(r'user_article_basic')

# 处理文本导致的字符串问题
def article_basic(row):
    row = row.split('\x01')
    for i in range(len(row)):
        if row[i] == 'false':
            row[i] = False
        elif row[i] == 'true':
            row[i] = True

    return row


user_article_basic = user_article_basic.map(article_basic).toDF(
['user_id', 'action_time', 'article_id', 'channel_id', 'shared', 'clicked', 'collected', 'exposure', 'read_time'])
print("用户文章行为信息", user_article_basic.take(10))


def user_topic(row):
    return row[0], row[3].split('\x02')


article_profile = spark.sparkContext.textFile(r'data\backup\article.db\article_profile')
article_profile = article_profile.map(lambda line: line.split('\x01'))
article_topic = article_profile.map(user_topic).toDF(['article_id', 'topics'])
print("文章主题信息", article_topic.take(10))
user_article_topic = user_article_basic.join(article_topic, how='left', on=['article_id'])
print("用户行为联合文章主题信息", user_article_topic.take(20))

import pyspark.sql.functions as F

# explode主题词拆开为一行
user_article_topic = user_article_topic.withColumn('topic', F.explode('topics')).drop('topics')
print(user_article_topic.take(10))


# 二、用户行为得分，各频道内各主题的分数
# 用户行为得分方案
def compute_user_label_weights(partitions):
    """ 计算用户标签权重
    """
    action_weight = {
        "read_min": 1,
        "read_middle": 2,
        "collect": 2,
        "share": 3,
        "click": 5
    }

    from datetime import datetime
    import numpy as np
    import json

    # 循环处理每个用户对应的每个主题词
    for row in partitions:
        # 计算时间衰减系数
        t = datetime.now() - datetime.strptime(row.action_time, '%Y-%m-%d %H:%M:%S')
        alpha = 1 / (np.log(t.days + 1) + 1)

        if row.read_time == '':
            read_t = 0
        else:
            read_t = int(row.read_time)

        # 计算阅读时间的行为分数
        read_score = action_weight['read_middle'] if read_t > 1000 else action_weight['read_min']

        # 计算各种行为的权重和并乘以时间衰减系数
        weights = alpha * (row.shared * action_weight['share'] + row.clicked * action_weight['click'] + row.collected *
                           action_weight['collect'] + read_score)

        yield row.user_id, {row.channel_id + ':' + row.topic: json.dumps(weights)}


user_profile = user_article_topic.rdd.mapPartitions(compute_user_label_weights).toDF(["user_id", "weights"])
user_profile = user_profile.groupBy("user_id").agg(F.collect_list('weights').alias('channel_weights'))
print("用户行为得分权重", user_profile.take(10))


# 三、用户各频道内选取最高k个得分最为用户特征
# 选取每个频道内最高k个标签特征
def frature_preprocess(row):
    user_weights = []
    for i in range(1, 26):
        try:
            initdict = {}
            for item in row.channel_weights:
                weights = dict(initdict, **item)
                initdict = weights

            channel_weights = sorted([float(weights[key]) for key in weights.keys() if key.split(':')[0] == str(i)], reverse=True)[:10]
            if len(channel_weights) < 10:
                channel_weights.extend([0.0] * (10 - len(channel_weights)))
            user_weights.append(channel_weights)
        except:
            user_weights.append([0.0] * 10)
    return row.user_id, user_weights


user_feature = user_profile.rdd.map(frature_preprocess).toDF(['user_id', 'user_features'])
print("用户特征", user_feature.take(10))
